import express from 'express';
const fs = require("fs");
const path = require("path");
const parseUrl = require('parseurl');
var etag = require('etag');
var fresh = require('fresh');

const faviconBase64String = '';

module.exports = favicon

function favicon(APP_ROOT_DIR: string) { 
 
  return (req: express.Request, res: express.Response, next: express.NextFunction) => {

    if (getPathname(req) !== '/favicon.ico') {
      next()
      return
    }

    if (req.method !== 'GET' && req.method !== 'HEAD') {
      res.statusCode = req.method === 'OPTIONS' ? 200 : 405
      res.setHeader('Allow', 'GET, HEAD, OPTIONS')
      res.setHeader('Content-Length', '0')
      res.end()
      return
    }

    const faviconPath = path.join(APP_ROOT_DIR, '..', 'favicon64.ico')
    const faviconBufferString = fs.existsSync(faviconPath) && fs.readFileSync(faviconPath).toString();
    const faviconIcon = Buffer.from(faviconBufferString || faviconBase64String, 'base64');

    send(req, res, faviconIcon);

  }

}

function send (req: express.Request, res: express.Response, icon: Buffer) {
	// Validate freshness
	if (isFresh(req, res)) {
		res.statusCode = 304;
		res.end();
		return;
	}
	// send favicon
  	res.statusCode = 200;
	res.setHeader('Content-Length', icon.length);
	res.setHeader('Content-Type', 'image/x-icon');
	res.setHeader("Cache-Control", "public, max-age=2592000");                // expiers after a month
	res.setHeader("ETag", etag(icon));                // expiers after a month
	res.setHeader("Expires", new Date(Date.now() + 2592000000).toUTCString());
	res.end(icon);
}

function isFresh (req: express.Request, res: express.Response) {
  return fresh(req.headers, {
    'etag': res.getHeader('ETag'),
    'last-modified': res.getHeader('Last-Modified')
  })
}

function getPathname (req: express.Request) {
  try {
    return parseUrl(req).pathname
  } catch (e) {
    return undefined
  }
}



